import{html,PolymerElement}from"../@polymer/polymer/polymer-element.js";import"../@polymer/paper-tabs/paper-tabs.js";class ApiNav extends PolymerElement{static get template(){return html`
    <style>
      paper-tabs {
        color: var(--paper-blue-900);
        --paper-tabs-selection-bar-color: var(--paper-blue-900);
        --paper-tab-ink: var(--paper-blue-900);
      }

      a {
        text-decoration: none;
        color: var(--paper-blue-900);
        display: flex;
        align-items: center;
        justify-content: center;
      }
    </style>
    <paper-tabs selected=[[selected]] link>
      <paper-tab><a href="https://statsplash.com">Home</a></paper-tab>
      <paper-tab><a href="https://statsplash.com/games">Games</a></paper-tab>
      <paper-tab><a href="https://statsplash.com/about">About</a></paper-tab>
    </paper-tabs>
    `}static get properties(){return{prop1:{type:String,value:"api-nav"},selected:{type:Number,value:0}}}ready(){super.ready();if(-1!=window.location.pathname.toLowerCase().indexOf("games")){this.selected=1}else if("about"===window.location.pathname.toLowerCase()){this.selected=2}else if("https://statsplash.com"===window.location.href){this.selected=0}else{this.selected=-1}}}window.customElements.define("api-nav",ApiNav);